         FileCreateStep fcs = new FileCreateStep();
         fcs.SourcePath = @"C:\Tests\BizUnit.Tests\Data\PO_MSFT001.xml";
         fcs.CreationPath = @"C:\Tests\BizUnit.Tests\Data\PO_MSFT001_%Guid%.xml";
